import React, { useState } from "react";
import LandingPage from "./components/LandingPage";
import Quiz from "./components/Quiz";
import Results from "./components/Results";
import "./styles.css";

function App() {
  const [currentScreen, setCurrentScreen] = useState("landing");
  const [fearScore, setFearScore] = useState(0);
  const [userName, setUserName] = useState(''); // New state for the user's name

  // The handleStartQuiz now accepts the name from the LandingPage
  const handleStartQuiz = (name) => {
    setUserName(name); // Save the user's name in state
    setCurrentScreen("quiz");
  };

  const handleEndQuiz = (score) => {
    setFearScore(score);
    setCurrentScreen("results");
  };

  return (
    <div className="app-container">
      {currentScreen === "landing" && (
        <LandingPage onStartQuiz={handleStartQuiz} />
      )}
      {currentScreen === "quiz" && <Quiz onEndQuiz={handleEndQuiz} userName={userName} />}
      {currentScreen === "results" && <Results fearScore={fearScore} userName={userName} />}
    </div>
  );
}

export default App;
