import React, { useState } from 'react';

// ... (your quizQuestions array is unchanged) ...
const quizQuestions = [
  //... all your quiz questions
];

// The Quiz component now accepts a 'userName' prop
const Quiz = ({ onEndQuiz, userName }) => {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);

  const currentQuestion = quizQuestions[currentQuestionIndex];

  const handleAnswerClick = (selectedScore) => {
    setScore(prevScore => prevScore + selectedScore);

    if (currentQuestionIndex === quizQuestions.length - 1) {
      onEndQuiz(score + selectedScore);
    } else {
      setCurrentQuestionIndex(prevIndex => prevIndex + 1);
    }
  };

  return (
    <div className="quiz-container">
      {/* Personalized welcome message */}
      <div className="question-text">Okay, {userName}, here we go!</div>
      <div className="question-text">{currentQuestion.question}</div>
      <div className="answer-options">
        {currentQuestion.answers.map((answer, index) => (
          <button
            key={index}
            className="answer-button"
            onClick={() => handleAnswerClick(answer.score)}
          >
            <span className="emoji">{answer.emoji}</span> {answer.text}
          </button>
        ))}
      </div>
      {currentQuestion.warning && (
        <div className="warning-message">{currentQuestion.warning}</div>
      )}
    </div>
  );
};

export default Quiz;
