import React, { useState } from "react";
import LandingPage from "./components/LandingPage";
import Quiz from "./components/Quiz";
import Results from "./components/Results";
import "./styles.css";

function App() {
  // state variable to control which screen is displayed
  // "landing", "quiz", or "results"
  const [currentScreen, setCurrentScreen] = useState("landing");
  
  // state variable to store the user's total fear score
  const [fearScore, setFearScore] = useState(0);

  // function to start the quiz
  const handleStartQuiz = () => {
    setCurrentScreen("quiz");
  };

  // function to end the quiz and show results
  const handleEndQuiz = (score) => {
    setFearScore(score);
    setCurrentScreen("results");
  };

  // The main App component renders different children based on the currentScreen state
  return (
    <div className="app-container">
      {currentScreen === "landing" && (
        <LandingPage onStartQuiz={handleStartQuiz} />
      )}
      {currentScreen === "quiz" && <Quiz onEndQuiz={handleEndQuiz} />}
      {currentScreen === "results" && <Results fearScore={fearScore} />}
    </div>
  );
}

export default App;
